#basic script to query TOPdesk instance for custoemr specific incident values. 
#Use when building RES ONE Service Store services to quickly have values on hand.

 add-type @"
using System.Net;
using System.Security.Cryptography.X509Certificates;
public class TrustAllCertsPolicy : ICertificatePolicy {
    public bool CheckValidationResult(
    ServicePoint srvPoint, X509Certificate certificate,
    WebRequest request, int certificateProblem) {
        return true;
    }
}
"@
[System.Net.ServicePointManager]::CertificatePolicy = New-Object TrustAllCertsPolicy


$AdminName = "$[AdminName]"
$AdminPWD = "$[Admin Password]"
$TOPdeskUrl = "$[TOPdesk URL]"
$token
$baseUri = $TOPdeskUrl+"/tas/api"

#$lastname = ''
#$firstname = 'Noah'
$email = '$[User email]'


$results = ''
$archived = 'false'
$operation = '$[operation]'

#Still need to build a task to pull archive reasons
#our current instance has list of reasons but they are in
#Dutch. 
$archive_reason = "64d2d84d-de01-5bc4-bf4a-08ec61e20d1e"


$token = Invoke-RestMethod -Method Get -Uri ($baseUri+"/login/operator") -Headers @{"Authorization" = "Basic "+[System.Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($AdminName+":"+$AdminPWD ))}

$headers = New-Object "System.Collections.Generic.Dictionary[[String],[String]]"
$headers.Add("Authorization", "TOKEN id=`"$token`"")

#need to set the search to look for currently archived users
if ($operation -eq 'unarchive'){
    $archived = 'true'
    }

$results = Invoke-RestMethod -Method Get -Uri ($baseUri+"/persons?email="+$email+"&archived="+$archived) -Headers $headers -ContentType 'application/json'

if ($results.id){
    Write-Host $results.id 
}else{
    Write-Host "Could not find a user with the email: "$email
    exit
}

$UserID = $results.id

$body = @"
{
   "id" : "$archive_reason"
}
"@


$results = Invoke-RestMethod -Method Put -Uri ($baseUri+"/persons/id/"+$UserID+"/"+$operation) -Headers $headers -Body $body -ContentType 'application/json'

