#basic script to query TOPdesk instance for custoemr specific incident values. 
#Use when building RES ONE Service Store services to quickly have values on hand.

 add-type @"
using System.Net;
using System.Security.Cryptography.X509Certificates;
public class TrustAllCertsPolicy : ICertificatePolicy {
    public bool CheckValidationResult(
    ServicePoint srvPoint, X509Certificate certificate,
    WebRequest request, int certificateProblem) {
        return true;
    }
}
"@

[System.Net.ServicePointManager]::CertificatePolicy = New-Object TrustAllCertsPolicy

$AdminName = "$[TOPdeskAdmin]"
$AdminPWD = "$[TOPdeskAdminPWD]"
$TOPdeskUrl = "$[TOPdeskURL]"
$token
$baseUri = $TOPdeskUrl+"/tas/api"


$userpassword = '$[New Password]'
$email = '$[Users Email]'

$token = Invoke-RestMethod -Method Get -Uri ($baseUri+"/login/operator") -Headers @{"Authorization" = "Basic "+[System.Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($AdminName+":"+$AdminPWD ))}

$headers = New-Object "System.Collections.Generic.Dictionary[[String],[String]]"
$headers.Add("Authorization", "TOKEN id=`"$token`"")


$results = ''
$results = Invoke-RestMethod -Method Get -Uri ($baseUri+"/persons?email="+$email) -Headers $headers -ContentType 'application/json'

if ($results.id){
    $UserID = $results.id
    $surName = $results.surName
    $branchID = $results.branch.id
}else{
    Write-Host "Could not find a user with the email: "$email
    exit
}

$body = @"
{
   "surName"  : "$surName",
   "password" : "$userpassword",
   "branch" :  {
         "id": "$branchID"
   }
}
"@

$results = Invoke-RestMethod -Method PUT -Uri ($baseUri+"/persons/id/"+ $UserID ) -Headers $headers -Body $body -ContentType 'application/json'

#basic script to query TOPdesk instance for custoemr specific incident values. 
#Use when building RES ONE Service Store services to quickly have values on hand.

 add-type @"
using System.Net;
using System.Security.Cryptography.X509Certificates;
public class TrustAllCertsPolicy : ICertificatePolicy {
    public bool CheckValidationResult(
    ServicePoint srvPoint, X509Certificate certificate,
    WebRequest request, int certificateProblem) {
        return true;
    }
}
"@
[System.Net.ServicePointManager]::CertificatePolicy = New-Object TrustAllCertsPolicy

$username = "^[TOPdesk administrator2]"
$password = "^[TOPdesk administrator password2]"
$TOPdeskUrl = "^[TOPdesk UR2]"

$token
$baseUri = $TOPdeskUrl+"/tas/api"
$output
$temp
$token = Invoke-RestMethod -Method Get -Uri ($baseUri+"/login/operator") -Headers @{"Authorization" = "Basic "+[System.Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($username+":"+$password ))}

$headers = New-Object "System.Collections.Generic.Dictionary[[String],[String]]"
$headers.Add("Authorization", "TOKEN id=`"$token`"")
$output = ""


$results = Invoke-RestMethod -Method Get -Uri ($baseUri+"/branches") -Headers $headers -ContentType 'application/json'


foreach($result in $results) {

    $output += $result.name +';' + $result.id + '|'

}

$output = $output -replace ".$"

Write-Host $output

